function run(lengths, topScrolls, edgeScrolls, frontScrolls, store, args){
    var count = 0;
    for(var i = 1; i < lengths.length; ++i){
        var l = Math.min(lengths[i], lengths[i-1]);
        if(l > 0){
            count += l/3;
        }
        if(lengths[i] == lengths[i-1]){
            count--;//这个时候要扣除一个
        }
    }
    return count;
}