function run(lengths, topScrolls, edgeScrolls, frontScrolls, store, args){
    var count = 0;
    count += lengths[0]/3 - 1;//第一排
    count += lengths[lengths.length - 1]/3 - 1;//最后一排
    for(var i = 1; i < lengths.length; ++i){
        var l = Math.abs(lengths[i] - lengths[i-1]);
        if(l > 0){
            count += l/3 - 1;
        }
    }
    return count;
}