/*
 棚内平横钢丝组通用计算公式。
 每6米一个，第一个在3米处出现。
 */

function run(lengths, topScrolls, edgeScrolls, frontScrolls, store, args){
    var count = 0;
    for(var i = 0; i < lengths.length; ++i){
        var length = lengths[i];//棚长
        count += Math.ceil((length+3)/6) - 1;//每隔6米一个组。因为第一个出现在3米处，所以这么操作
    }
    return count;
}