function run(lengths, topScrolls, edgeScrolls, frontScrolls, store, args){
    var pre = 0;//先设置pre为0
    var count = 0;
    for(var i = 0; i < lengths.length; ++i){
        var length = Math.max(pre, lengths[i]);//取出最大数
        pre = lengths[i];//重新设置pre
        var c = Math.ceil((length - length%600)/600);
//        console.log(length, c);
        count += c;
        //立柱的间隔是600，但是立柱之间有根附加立柱，有可能在最后两根立柱之间的距离变成了300，例如2700长度。
    }
    //还得再计算最后一下
    var c = Math.ceil((pre - pre%600)/600);
//    console.log(pre, c);
    count += c;
    return count;
}