router.post("/shed/proj/calComp", function(req, res, next){
    var projId = req.body.id;
    req.dbClient.list("shed", {projId : projId}, function(err, sheds){
        async.map(sheds, function(shed, cb1){
            calBySche(req.dbClient, shed, cb1);
        }, function(err, results){
            var calList = [];
            var calMap = {};
            for(var i = 0; i < results.length; ++i){
                var shedCalList = results[i];
                for(var j = 0; j < shedCalList.length; ++j){
                    var compInfo = shedCalList[j];
                    var compId = compInfo.id;
                    var info = calMap[compId];
                    if(info){
                        info.count += compInfo.count;
                    }else{
                        calMap[compId] = compInfo;
                    }
                }
            }
            for(var compId in calMap){
                calList.push(calMap[compId]);
            }
            res.send(calList);
        });
    });
});
