//将字符串转成int数组
function strToIntArr(str){
    var arr = str.split(",");
    var results = [];
    for(var i = 0; i < arr.length; ++i){
        results.push(parseInt(arr[i]));
    }
    return results;
}
//初始化shed数据
function initShed(shed){
    shed.count = shed.count || 1;
    if(typeof shed.lengths == "string") shed.lengths = strToIntArr(shed.lengths);
    if(typeof shed.topScrolls == "string") shed.topScrolls = strToIntArr(shed.topScrolls);
    if(typeof shed.edgeScrolls == "string") shed.edgeScrolls = strToIntArr(shed.edgeScrolls);
    if(typeof shed.frontScrolls == "string") shed.frontScrolls = strToIntArr(shed.frontScrolls);
}
function calBySche(client, shed, cb){
    initShed(shed);
    var scheId = shed.scheId;
    async.parallel([
        function(cb1){
            //获取到零件公式内容列表
            var sqlStr = "select e.exp, s.name scheName, cex.* from sche s, compexp cex, exp e where s.id = ? and s.id = cex.scheId and cex.expId = e.id order by cex.seq";
            client.query(sqlStr, [scheId], cb1);
        },
        function(cb1){
            //获取到相关零件列表
            var sqlStr = "select c.name, c.code, c.isBase, c.mates, c.spec, c.unit, s.name scheName, cex.* from sche s, compexp cex, comp c where s.id = ? and s.id = cex.scheId and cex.compId = c.id";
            client.query(sqlStr, [scheId], function(err, list){
                if(err) return cb1(err);
                var compMap = {};
                for(var i = 0; i < list.length; ++i){
                    var comp = list[i];
                    if(compMap[comp.compId]) continue;
                    compMap[comp.compId] = comp;
                }
                cb1(null, compMap);
            });
        },
        function(cb1){
            //获取到有关联的子零件列表
            var sqlStr = "select c.id parentId, c2.id compId, cd.count, c2.* from sche s, compexp cex, comp c, compdtl cd, comp c2 where s.id = ? and s.id = cex.scheId and cex.compId = c.id and c.isBase != 1 and cd.id = c.id and c2.id = cd.childId";
            client.query(sqlStr, [scheId], function(err, list){
                if(err) return cb1(err);
                var dtlMap = {};
                for(var i = 0; i < list.length; ++i){
                    var dtl = list[i];
                    var parentId = dtl.parentId;
                    var dtlList = dtlMap[parentId];
                    if(!dtlList){
                        dtlList = dtlMap[parentId] = [];
                    }
                    dtlList.push(dtl);
                }
                cb1(null, dtlMap);
            });
        }
    ], function(err, results){
        if(err) return cb(err);
        var expInfoList = results[0];
        var compMap = results[1];
        var dtlMap = results[2];

        var result = {};
        var compInfoMap = {};
        try{
            for(var i = 0; i < expInfoList.length; ++i){
                var info = expInfoList[i];
                var exp = info.exp;
                var compId = info.compId;
                var comp = compMap[compId];
                if(!compInfoMap[compId]) {
                    compInfoMap[compId] = {
                        id : compId,
                        code : comp.code,
                        name : comp.name,
                        isBase : comp.isBase,
                        spec : comp.spec,
                        mates : comp.mates,
                        unit : comp.unit,
                        remark : comp.remark
                    }
                }
                exp = eval('(' + exp + ')');
                var value = shed.count * exp(shed.lengths, shed.topScrolls, shed.edgeScrolls, shed.frontScrolls)
                result[compId] = (result[compId] || 0) + value;
                if(!comp.isBase){
                    var dtlList = dtlMap[compId] || [];
                    for(var j = 0; j < dtlList.length; ++j){
                        var dtl = dtlList[j];
                        var dtlCompId = dtl.compId;
                        if(!compInfoMap[dtlCompId]) {//初始化数据
                            compInfoMap[dtlCompId] = {
                                id : dtlCompId,
                                code : dtl.code,
                                name : dtl.compName,
                                isBase : dtl.isBase,
                                spec : dtl.spec,
                                mates : dtl.mates,
                                unit : dtl.unit,
                                remark : dtl.remark
                            }
                        }
                        result[dtlCompId] = (result[dtlCompId] || 0) + value * dtl.count;
                    }
                }
            }

            var calList = [];
            for(var compId in result){
                var compInfo = compInfoMap[compId];
                if(!compInfo.isBase) continue;//非基础零件则不算在内
                compInfo.count = result[compId];
                calList.push(compInfo);
            }
            cb(null, calList);
        }catch(e){
            return cb(e);
        }
    });
}

//++++++++++++++++++++++sche 结束+++++++++++++++++++++++++++++++++


